mapper.update:
UPDATE tbl_fight
SET
win_user_id=#{C.winUserId},
send_vote=#{C.sendVote},
receive_vote=#{C.receiveVote}
WHERE fight_id=#{C.fightId}

mapper.select:
SELECT * FROM tbl_fight
WHERE win_user_id IS NULL
LIMIT #{start},#{amount}

SELECT COUNT(*)
FROM tbl_fight
WHERE send_user_id = #{send}
AND receive_user_id = #{receive}
AND win_user_id IS NULL
